import { useState, useEffect } from 'react'
import UsuarioService from '../services/UsuarioService'

const FormLogin = () => {

    const formData = {
        mail: '',
        pwd: ''
    }

    const [mail,setMail] = useState('');
    const [user,setUser] = useState(formData)

    const handleSubmit = (e) =>{
        e.preventDefault();

        UsuarioService.setMailAuth(mail).then(res => {
            console.log(res)
        })
    }

    const handleInputChange = (e) => {
        const {mail,pwd} = event.target;
        setMail(mail)
        setUser({mail,pwd})
    }

    return (
        <form class="col-12 mb-3" onSubmit={handleSubmit}>
            <div className="form-group">
                <input type="text" className="form-control" placeholder="Correo de Usuario" name="mail" id="mail"/>
            </div>
            <div className="form-group">
                <input type="password" className="form-control" placeholder="Correo de Usuario" name="pwd" id="pwd"/>
            </div>
            <button type="button" className="btn btn-primary ">Primary</button>

        </form>
    )
}

export default FormLogin;
