import { useState, useEffect } from 'react'

import styles from '../css/login.module.css'

import Layout from '../component/Layout'
import CardItem from '../component/CardItemList'
import FilterCredito from '../component/FilterCredito'

import CreditoService from "../services/CreditoService"


const Creditos = () => {

    useEffect(() => {
        getAllCredits(0);
    }, [])

    const [creditos, setCreditos] = useState([]);
    const [estadoIndex,setEstadoIndex] = useState(0);

    const getAllCredits = (index) => {
        console.log(index)
        CreditoService.getByEstado(index)
            .then(res => {
                setCreditos([...res.data])
            })
            .catch(console.log)
    }

    const refreshCreditos = async (index)=>{
        await setCreditos([]);
        getAllCredits(index);
    }

    return (
        <Layout>
            <div className={`text-center ${styles["container"]}`}>
                <FilterCredito changeIndex={setEstadoIndex} updateTable={refreshCreditos}/>
                {estadoIndex}
                <ul className="list-group">
                    <li className="list-group-item d-flex justify-content-between align-items-center">
                        <div className="col-9 row">
                            <div className="col-1">
                                Foto
                            </div>
                            <div className="col-2">
                                Nombre
                            </div>
                            <div className="col-4">
                                Monto
                            </div>
                            <div className="col-1">
                                IDK
                            </div>
                        </div>
                    </li>
                    {creditos.map((credito) => (
                        <CardItem key={credito.id} credito={credito} currentIndex={estadoIndex} updateTable={refreshCreditos}/>
                    ))}
                </ul>
            </div>
        </Layout>
    );
}
export default Creditos
