import { useEffect, useState } from 'react'
import { makeStyles } from '@material-ui/core/styles';
import { Table, TableContainer, TableHead, TableCell, TableBody, TableRow, Modal, Button, TextField } from '@material-ui/core';

import ApiService from "../services/ApiService"

import styles from '../css/modal.module.css'

const AnalisisCredito = (props) => {



    const [detallesSBS, setDetallesSBS] = useState([]);
    const [detallesSenitel, setDetallesSenitel] = useState([]);
    const [isRendered, SetisRendered] = useState(true);

    const selectModalColor = (score) => {

        switch(score[0]){
            case 'Bueno':   return 'text-white bg-success'
                break;
            case 'Regular': return 'text-white bg-warning'
                break
            case 'Malo':    return 'text-white bg-danger'
                break
            default:        return 'bg-light'
                break;
        }



    }

    const { dni, status } = props.showModal;

    const getDetailsDNI = (dni) => {
        if (isRendered) {
            ApiService.getSbs(dni)
                .then(res => {
                    SetisRendered(false);
                    setDetallesSBS([res.data])
                })
                .catch(console.log)
            ApiService.getSentinel(dni)
                .then(res => {
                    setDetallesSenitel([res.data])
                })
                .catch(console.log)
        }
    }

    const closeModal = () => {
        props.configData({
            status: false
        });
        SetisRendered(true)
    }

    return (
        <Modal open={status} className={styles["modal"]}>
            <div className="modal-dialog modal-dialog-centered" role="document">
                <div className="modal-content">
                    <div className="modal-header">
                        <h5 className="modal-title" id="exampleModalLongTitle">Detalle del Cliente</h5>
                    </div>
                    <div className="modal-body">
                        {(status == false) ? null : getDetailsDNI(dni)}
                        {detallesSBS[0] != null && detallesSBS[0] != 0 ? (
                            <div>
                                {detallesSBS[0].totalDeuda}
                                {detallesSenitel[0].score[0]}
                                <div class={`card ${selectModalColor(detallesSenitel[0].score[0])}`}>
                                    <div class="card-header">
                                        Featured
                                    </div>
                                    <ul class="list-group list-group-flush">
                                        <li class="list-group-item">Cras justo odio</li>
                                    </ul>
                                </div>
                            </div>
                        ) : "null"}
                    </div>
                    <div className="modal-footer">
                        <button type="button" className="btn btn-primary" onClick={closeModal}>Salir</button>
                    </div>
                </div>

            </div>

        </Modal>

    )
};

export default AnalisisCredito;
