import { parseCookies, setCookie } from 'nookies'

const setCookies = (name,value) => {
    setCookie(null, name, value, {
        maxAge: 10800,
        path: '/',
      })
}

const getCookies = () => {
    return parseCookies()
}

export default {
    getCookies,
    setCookies
}
