import { useState, useEffect } from 'react'
import UsuarioService from '../services/UsuarioService'

const FormLogin = () => {

    const formData = {
        state:{
            mail: '',
            pwd: ''
        }
    }

    const [mail,setMail] = useState({});
    const [user,setUser] = useState(formData)

    const handleSubmit = (e) =>{
        e.preventDefault();

        console.log(mail)
        UsuarioService.setMailAuth(mail).then(res => {
            console.log(res)
        }).catch(console.log)
    }

    const handleInputChange = (e) => {
        const {mail,pwd} = event.target;
        console.log(pwd)
        setMail({mail: 'maria@credito.com'})
        setUser({mail,pwd})
    }

    return (
        <form className="col-12 mb-3" onSubmit={handleSubmit}>
            <div className="form-group">
                <input type="text" 
                        className="form-control" 
                        placeholder="Correo de Usuario"
                        name="mail"
                        onChange={handleInputChange}
                        value={formData.state.mail}
                        id="mail"/>
            </div>
            <div className="form-group">
                <input type="password" 
                        className="form-control" 
                        placeholder="Correo de Usuario" 
                        name="pwd"
                        value={user.pwd}
                        onChange={handleInputChange}
                        id="pwd"/>
            </div>
            <button type="submit" className="btn btn-primary">Primary</button>

        </form>
    )
}

export default FormLogin;
